INSERT INTO `wp_apporder_snippets` (`id`, `name`, `description`, `code`, `tags`, `scope`, `priority`, `active`) VALUES
(1, 'Example HTML shortcode', 'This is an example snippet for demonstrating how to add an HTML shortcode. You can remove it, or edit it to add your own content.', '\nadd_shortcode( \'shortcode_name\', function () {\n\n	$out = \'<p>write your HTML shortcode content here</p>\';\n\n	return $out;\n} );', 'shortcode', 'global', 10, 0),
(2, 'Example CSS snippet', 'This is an example snippet for demonstrating how to add custom CSS code to your website. You can remove it, or edit it to add your own content.', '\nadd_action( \'wp_head\', function () { ?>\n	<style>\n\n		/* write your CSS code here */\n\n	</style>\n<?php } );\n', 'css', 'front-end', 10, 0),
(3, 'Example JavaScript snippet', 'This is an example snippet for demonstrating how to add custom JavaScript code to your website. You can remove it, or edit it to add your own content.', '\nadd_action( \'wp_head\', function () { ?>\n	<script>\n\n		/* write your JavaScript code here */\n\n	</script>\n<?php } );\n', 'javascript', 'front-end', 10, 0),
(4, 'Order snippets by name', 'Order snippets by name by default in the snippets table. You can remove it, or edit it to add your own content.', '\nadd_filter( \'code_snippets/list_table/default_orderby\', function () {\n	return \'name\';\n} );\n', 'code-snippets-plugin', 'admin', 10, 0),
(5, 'Menu Remove', '', '        \nfunction remove_submenus() { \n\n$user = wp_get_current_user();\n   if ( !in_array( \'administrator\', (array) $user->roles ) ) {\n                \nglobal $submenu;\nremove_menu_page( \'index.php\' );                  //Dashboard\n	remove_menu_page( \'jetpack\' );                    //Jetpack* \n	remove_menu_page( \'edit.php\' );                   //Posts\n	remove_menu_page( \'upload.php\' );                 //Media\n	remove_menu_page( \'edit.php?post_type=page\' );    //Pages\n	remove_menu_page( \'edit-comments.php\' );          //Comments\n	remove_menu_page( \'themes.php\' );                 //Appearance\n	remove_menu_page( \'plugins.php\' );                //Plugins\n	remove_menu_page( \'tools.php\' );                  //Tools\n	remove_menu_page( \'options-general.php\' );        //Settings\n	remove_menu_page( \'edit.php\' );        //Elementor\n \n   } \n   \n } \nadd_action(\'admin_menu\', \'remove_submenus\'); ', '', 'global', 10, 1),
(6, 'Widget Remove', '', 'function example_dashboard_widget_function() {\n	// Display whatever it is you want to show\n	echo \"Hello World, I\'m a great Dashboard Widget\";\n} \n \n// Create the function use in the action hook\nfunction example_add_dashboard_widgets() {\n	wp_add_dashboard_widget(\'example_dashboard_widget\', \'Example Dashboard Widget\', \'example_dashboard_widget_function\');\n}\n// Hoook into the \'wp_dashboard_setup\' action to register our other functions\nadd_action(\'wp_dashboard_setup\', \'example_add_dashboard_widgets\' );\n\nfunction example_remove_dashboard_widgets() {\n	// Globalize the metaboxes array, this holds all the widgets for wp-admin\n \n	global $wp_meta_boxes;\n \n	// Remove the incomming links widget\n	unset($wp_meta_boxes[\'dashboard\'][\'normal\'][\'core\'][\'dashboard_incoming_links\']);	\n \n	// Remove right now\n	unset($wp_meta_boxes[\'dashboard\'][\'normal\'][\'core\'][\'dashboard_right_now\']);\n	unset($wp_meta_boxes[\'dashboard\'][\'side\'][\'core\'][\'dashboard_primary\']);\n	unset($wp_meta_boxes[\'dashboard\'][\'side\'][\'core\'][\'dashboard_secondary\']);\n}\n \n// Hoook into the \'wp_dashboard_setup\' action to register our function\nadd_action(\'wp_dashboard_setup\', \'example_remove_dashboard_widgets\' );', '', 'global', 10, 1),
(7, 'Remove Footer and wodpress logo admin bar', '', 'function remove_footer_admin () {\n    echo \"\";\n} \n\nadd_filter(\'admin_footer_text\', \'remove_footer_admin\');\n\nfunction example_admin_bar_remove_logo() {\n    global $wp_admin_bar;\n    $wp_admin_bar->remove_menu( \'wp-logo\' );\n}\nadd_action( \'wp_before_admin_bar_render\', \'example_admin_bar_remove_logo\', 0 );', '', 'global', 10, 1),
(8, 'notification', 'This is an example snippet for demonstrating how to add an HTML shortcode. You can remove it, or edit it to add your own content.', 'add_shortcode( \'notification\', function () {\r\n\r\n	$out = \'<p>write your HTML shortcode content here</p>\';\r\n\r\n	return $out;\r\n} );', 'shortcode', 'global', 10, 0),
(9, 'redirect', '', 'function soi_login_redirect($redirect_to, $request, $user)\r\n{\r\n    return (is_array($user->roles) && in_array(\'administrator\', $user->roles)) ? admin_url() : site_url();\r\n} \r\nadd_filter(\'login_redirect\', \'soi_login_redirect\', 10, 3);', '', 'global', 10, 1),
(10, 'shortcode for redirect', 'This is an example snippet for demonstrating how to add an HTML shortcode. You can remove it, or edit it to add your own content.', 'add_shortcode( \'redirect_dashboard\', function () {\r\n\r\n	wp_redirect(site_url());\r\n} );', 'shortcode', 'global', 10, 0);
